function createStyles(keyframes) {
  var initialKeyframes = {};
  var transformKeys = [];

  for (var key in keyframes) {
    var value = keyframes[key];

    if (isTransform(key)) {
      if (transformAlias[key]) key = transformAlias[key];
      transformKeys.push(key);
      key = asTransformCssVar(key);
