    if (definition) {
      initialKeyframe = isNumber(value) ? definition.toDefaultUnit(value) : value;
    }

    initialKeyframes[key] = initialKeyframe;
  }

  if (transformKeys.length) {
    initialKeyframes.transform = buildTransformTemplate(transformKeys);
