import React, { useContext, useEffect, useState } from 'react';
import Chart from 'react-apexcharts';
import LinearProgress, {
    linearProgressClasses,
} from '@mui/material/LinearProgress';
import { Alert, Grid, styled } from '@mui/material';
import { Box, Container } from '@mui/system';
import FoodCard from '../../Components/FoodCard';
import BottomNavBar from '../../Components/BottomNavBar';
import { API } from '../../services/apis';
import { useSnackbar } from 'notistack';
import { formatAMPM } from '../../utils/utils';
import { FullPageLoading } from '../../Components/LoadingSpinner';
import { AppContext } from '../../Context/AppContext';
import CloseIcon from '@mui/icons-material/Close';
import PlayCircleFilledIcon from '@mui/icons-material/PlayCircleFilled';
import Joyride, { CallBackProps, STATUS, Step } from 'react-joyride';
import { motion } from 'framer-motion';

const tourMotion = {
    offscreen: {
        x: -300,
    },
    onscreen: {
        x: 0,
        scale: 1,

        transition: {
            type: 'spring',
            duration: 1,
        },
    },
};

const ProtienLinearProgress = styled(LinearProgress)(({ theme }) => ({
    height: 10,
    borderRadius: 5,
    [`&.${linearProgressClasses.colorPrimary}`]: {
        backgroundColor:
            theme.palette.grey[theme.palette.mode === 'light' ? 200 : 900],
    },
    [`& .${linearProgressClasses.bar}`]: {
        borderRadius: 5,
        backgroundColor: '#DA0037',
    },
}));
const FiberLinearProgress = styled(LinearProgress)(({ theme }) => ({
    height: 10,
    borderRadius: 5,
    [`&.${linearProgressClasses.colorPrimary}`]: {
        backgroundColor:
            theme.palette.grey[theme.palette.mode === 'light' ? 200 : 900],
    },
    [`& .${linearProgressClasses.bar}`]: {
        borderRadius: 5,
        backgroundColor: '#3CCF4E',
    },
}));
const CarbsLinearProgress = styled(LinearProgress)(({ theme }) => ({
    height: 10,
    borderRadius: 5,
    [`&.${linearProgressClasses.colorPrimary}`]: {
        backgroundColor:
            theme.palette.grey[theme.palette.mode === 'light' ? 200 : 900],
    },
    [`& .${linearProgressClasses.bar}`]: {
        borderRadius: 5,
        backgroundColor: '#876445',
    },
}));
const FatsLinearProgress = styled(LinearProgress)(({ theme }) => ({
    height: 10,
    borderRadius: 5,
    [`&.${linearProgressClasses.colorPrimary}`]: {
        backgroundColor:
            theme.palette.grey[theme.palette.mode === 'light' ? 200 : 900],
    },
    [`& .${linearProgressClasses.bar}`]: {
        borderRadius: 5,
        backgroundColor: '#8758FF',
    },
}));

const MAX_CALORIES = 2000;
function calorieProgressColor({ value, seriesIndex, w }) {
    if (value < 35) {
        return '#42855B';
    } else if (value >= 35 && value < 50) {
        return '#FFE162';
    } else if (value >= 50 && value < 80) {
        return '#F76E11';
    } else {
        return '#B20600';
    }
}
const Dashboard = () => {
    const { nutrients, setnutrients, todayFoodItems } = useContext(AppContext);
    const [tourRun, settourRun] = useState(false);
    const { isLoading, isTourTaken, setisTourTaken } = useContext(AppContext);
    const todaysCaloriesPercent =
        nutrients.calorie > MAX_CALORIES
            ? 100
            : (nutrients.calorie / MAX_CALORIES) * 100;
    const CALORIES = parseInt(Number(nutrients.calorie));

    const steps = [
        {
            target: '.calorieBar',
            content: "Check your today's calories in this progress gauge",
            disableBeacon: true,
        },
        {
            target: '.nutrientBars',
            content:
                'Know the nutrient quantities you are taking and stay in limit',
            disableBeacon: true,
        },
        {
            target: '.todaysFood',
            content: 'Find what you have taken today',
            disableBeacon: true,
        },
        {
            target: '#reports',
            content:
                'Know and Analyse your food and calorie consumption of past week',
            disableBeacon: true,
        },
        {
            target: '#scan',
            content:
                'Scan your food and know the facts and add it to your dairy',
            disableBeacon: true,
        },
        {
            target: '#blogs',
            content:
                'Learn about staying healthy, Follow dietary meal plans provided by experts',
            disableBeacon: true,
        },
    ];

    const handleTour = (data) => {
        const { status } = data;
        const finishedStatuses = [STATUS.FINISHED, STATUS.SKIPPED];

        if (finishedStatuses.includes(status)) {
            settourRun(false);
        }
    };

    return (
        <motion.div
            initial='offscreen'
            whileInView='onscreen'
            viewport={{ once: true }}
            style={{
                background: 'var(--backgroundColor)',
                paddingBottom: '6rem',
            }}
        >
            <FullPageLoading isLoading={false} />
            <div
                style={{
                    boxShadow: 'rgba(0, 0, 0, 0.2) 0px 3px 3px 0px',
                    display: 'flex',
                    justifyContent: 'center',
                }}
            >
                <h1
                    style={{
                        display: 'inline-block',
                        margin: '0 auto',
                        padding: '0.8rem',
                    }}
                >
                    Dashboard
                </h1>
            </div>

            <motion.div
                variants={tourMotion}
                style={
                    !isTourTaken && !isLoading
                        ? {
                              backgroundColor: '#fff',
                              padding: '0.3rem',
                              display: 'inline-block',
                              border: '0px solid var(--themecolor)',
                              borderRadius: '10px',
                              position: 'absolute',
                              zIndex: 100,
                              top: 0,
                              left: 0,
                              right: 0,
                              bottom: 0,
                              margin: 'auto',
                              width: '150px',
                              height: '120px',
                              boxShadow: '0 0 0 9999px rgba(0, 0, 0, 0.6)',
                          }
                        : { display: 'none' }
                }
            >
                {!isLoading && (
                    <>
                        <div
                            style={{
                                display: 'flex',
                                justifyContent: 'flex-end',
                            }}
                        >
                            <button
                                style={{
                                    backgroundColor: 'transparent',
                                    border: 'transparent',
                                }}
                                onClick={(e) => setisTourTaken(true)}
                            >
                                <CloseIcon />
                            </button>
                        </div>
                        <div style={{ padding: '0 18px 10px 18px' }}>
                            <div style={{ fontSize: '18px' }}>Take a tour</div>

                            <button
                                style={{
                                    padding: '4px 10px',
                                    width: '100%',
                                    borderRadius: '6px',
                                    fontSize: '20px',
                                    color: 'white',
                                    background: 'var(--themecolor)',
                                    border: '1px solid var(--themecolor)',
                                    marginTop: '5px',
                                    display: 'flex',
                                    justifyContent: 'center',
                                    alignItems: 'center',
                                }}
                                onClick={(e) => {
                                    e.preventDefault();
                                    settourRun(true);
                                    setisTourTaken(true);
                                }}
                            >
                                <span style={{ marginRight: '5px' }}>
                                    start
                                </span>{' '}
                                <PlayCircleFilledIcon />
                            </button>
                        </div>
                    </>
                )}
            </motion.div>

            <Joyride
                callback={handleTour}
                continuous
                showProgress
                showSkipButton
                disableScrolling={true}
                hideCloseButton
                steps={steps}
                scrollToFirstStep
                run={tourRun}
                spotlightClicks={false}
                styles={{
                    options: {
                        zIndex: 10000,
                        primaryColor: 'var(--themecolor)',
                    },
                }}
            />
            <div>
                <Chart
                    className='calorieBar'
                    type='radialBar'
                    series={[todaysCaloriesPercent]}
                    height={400}
                    options={{
                        labels: ['calories'],
                        CALORIES: CALORIES,
                        plotOptions: {
                            radialBar: {
                                hollow: {
                                    margin: 0,
                                    size: '70%',
                                    background: '#F9F9F9',
                                    boxShadow:
                                        '0 4px 8px 0 rgba(0, 0, 0, 0.18), 0 6px 20px 0 rgba(0, 0, 0, 0.19)',
                                    dropShadow: {
                                        enabled: true,
                                        top: 0,
                                        left: 0,
                                        blur: 4,
                                        opacity: 0.24,
                                    },
                                },
                                track: {
                                    background: '#fff',
                                    dropShadow: {
                                        enabled: true,
                                        top: 2,
                                        left: 0,
                                        blur: 4,
                                        opacity: 0.5,
                                    },
                                },
                                dataLabels: {
                                    name: {
                                        color: calorieProgressColor({
                                            value: todaysCaloriesPercent,
                                        }),
                                        fontSize: '1.5rem',
                                        offsetY: 40,
                                        fontWeight: 300,
                                    },
                                    value: {
                                        color: calorieProgressColor({
                                            value: todaysCaloriesPercent,
                                        }),
                                        fontSize: '4.5rem',
                                        fontWeight: 700,
                                        margin: '1rem',
                                        offsetY: -10,
                                        show: true,
                                        formatter: (value) => {
                                            return CALORIES;
                                        },
                                    },
                                },
                            },
                        },
                        fill: {
                            colors: [calorieProgressColor],
                        },
                        stroke: {
                            lineCap: 'round',
                        },
                    }}
                ></Chart>
            </div>
            <Container maxWidth='sm'>
                <Box
                    className='nutrientBars'
                    sx={{
                        mx: '1rem',
                        p: '1rem',
                        backgroundColor: '#fff',
                        borderRadius: '8px',
                        boxShadow:
                            '0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)',
                    }}
                >
                    <div
                        style={{
                            marginBottom: '0.2rem',
                            display: 'flex',
                            alignItems: 'center',
                        }}
                    >
                        <div
                            style={{
                                backgroundColor: '#DA0037',
                                color: 'red',
                                width: '9px',
                                height: '9px',
                                borderRadius: '100%',
                            }}
                        ></div>
                        <p style={{ marginLeft: '10px' }}>
                            Protein{' '}
                            <span>
                                [{nutrients.protiens}g / {nutrients.maxprotiens}
                                g]
                            </span>
                        </p>
                    </div>
                    <ProtienLinearProgress
                        variant='determinate'
                        value={
                            nutrients.protiens > nutrients.maxprotiens
                                ? 100
                                : (nutrients.protiens / nutrients.maxprotiens) *
                                  100
                        }
                    />
                    <div
                        style={{
                            marginTop: '0.7rem',
                            marginBottom: '0.2rem',
                            display: 'flex',
                            alignItems: 'center',
                        }}
                    >
                        <div
                            style={{
                                backgroundColor: '#3CCF4E',
                                color: 'red',
                                width: '9px',
                                height: '9px',
                                borderRadius: '100%',
                            }}
                        ></div>
                        <p style={{ marginLeft: '10px' }}>
                            Calcium{' '}
                            <span>
                                [{nutrients.calcium}mg / {nutrients.maxcalcium}
                                mg]
                            </span>
                        </p>
                    </div>
                    <FiberLinearProgress
                        variant='determinate'
                        value={
                            nutrients.calcium > nutrients.maxcalcium
                                ? 100
                                : (nutrients.calcium / nutrients.maxcalcium) *
                                  100
                        }
                    />
                    <div
                        style={{
                            marginTop: '0.7rem',
                            marginBottom: '0.2rem',
                            display: 'flex',
                            alignItems: 'center',
                        }}
                    >
                        <div
                            style={{
                                backgroundColor: '#876445',
                                color: 'red',
                                width: '9px',
                                height: '9px',
                                borderRadius: '100%',
                            }}
                        ></div>
                        <p style={{ marginLeft: '10px' }}>
                            Carbohydrates{' '}
                            <span>
                                [{nutrients.carbs}g / {nutrients.maxcarbs}g]
                            </span>
                        </p>
                    </div>
                    <CarbsLinearProgress
                        variant='determinate'
                        value={
                            nutrients.carbs > nutrients.maxcarbs
                                ? 100
                                : (nutrients.carbs / nutrients.maxcarbs) * 100
                        }
                    />
                    <div
                        style={{
                            marginTop: '0.7rem',
                            marginBottom: '0.2rem',
                            display: 'flex',
                            alignItems: 'center',
                        }}
                    >
                        <div
                            style={{
                                backgroundColor: '#8758FF',
                                color: 'red',
                                width: '9px',
                                height: '9px',
                                borderRadius: '100%',
                            }}
                        ></div>
                        <p style={{ marginLeft: '10px' }}>
                            Fats{' '}
                            <span>
                                [{nutrients.fats}g / {nutrients.maxfats}g]
                            </span>
                        </p>
                    </div>
                    <FatsLinearProgress
                        variant='determinate'
                        value={
                            nutrients.fats > nutrients.maxfats
                                ? 100
                                : (nutrients.fats / nutrients.maxfats) * 100
                        }
                    />
                </Box>
                <div className='todaysFood'>
                    <h2 style={{ marginTop: '1.5rem', marginBottom: '1rem' }}>
                        Today's Food
                    </h2>
                    {todayFoodItems.length === 0 && (
                        <Alert severity='info'>
                            No foods consumed today. Click on camera icon to
                            capture your food.
                        </Alert>
                    )}
                    <Grid columnSpacing={'1rem'} container>
                        {todayFoodItems.map((item, idx) => {
                            return (
                                <Grid key={idx} item sm={6} xs={12}>
                                    <FoodCard
                                        key={item.ID}
                                        calories={item.CALORIE.toFixed(2)}
                                        carbohydrates={item.CARBOHYDRATES.toFixed(
                                            2
                                        )}
                                        fats={item.FAT.toFixed(2)}
                                        image_url={item.IMAGE}
                                        proteins={item.PROTEINS.toFixed(2)}
                                        time={formatAMPM(
                                            new Date(item.CONSUMED_ON)
                                        )}
                                        calcium={item.CALCIUM.toFixed(2)}
                                    />
                                </Grid>
                            );
                        })}
                    </Grid>
                </div>
            </Container>
        </motion.div>
    );
};

export default Dashboard;
